import React from 'react';

import { Chevron } from 'gca-react-components';

import './SortControl.scss';

interface SortControlProps {
  name: string;
  onUp: () => void;
  onDown: () => void;
  active?: 'up' | 'down';
}

export function SortControl(props: SortControlProps) {
  const { active, name, onUp, onDown } = props;

  return (
    <div className={`sort-control`}>
      <span onClick={onUp} data-testid={`${name}-up`}>
        <Chevron direction="up" color={active === 'up' ? '#000' : '#ccc'} />
      </span>
      <span onClick={onDown} data-testid={`${name}-down`}>
        <Chevron direction="down" color={active === 'down' ? '#000' : '#ccc'} />
      </span>
    </div>
  );
}
