import React from 'react';
import { injectIntl } from 'react-intl';


export default injectIntl(({
  intl,
  user,
}: any) => {
  if (!user || !user.roles) {
    return null;
  }

  return user.roles.map((roleUrn: string) => (
    <div key={roleUrn}>{intl.formatMessage({ id: roleUrn })}</div>
  ));
});
