import { formatPhone } from './inputHelpers';


describe('formatPhone', () => {
  it('should return value passed in if falsy', () => {
    expect(formatPhone()).toBe(undefined);
    expect(formatPhone(null)).toBe(null);
  });

  it('should remove non-digit characters', () => {
    const mixedCharacters = '123abc';
    const onlyDigits = formatPhone(mixedCharacters);
    expect(onlyDigits).toBe('123');
  });
});
