import { SortOrder, SortParameters } from '../store/types';


export function propsToQuerystring(props: any ): string {
  return `?${Object.keys(props)
    .filter(key => props[key] !== undefined && props[key].toString() !== '')
    .map(searchKey => `${searchKey}=${encodeURIComponent(props[searchKey])}`)
    .join('&')}`;
}

export function sortedToProps( sorted: {[id: string]: SortOrder} ): SortParameters {
  const sort = Object.keys(sorted)[0];
  return {
    order: sorted[sort],
    sort,
  }
}
