import React from 'react';
import { render } from 'react-testing-library';
import { CheckboxListContainer } from './CheckboxListContainer';

test('Should render', async () => {
  const result = await render(<CheckboxListContainer />);

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render with a predefined maxHeight', async () => {
  const result = await render(<CheckboxListContainer maxHeight={500} />);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render with default maxHeight when no maxHeight specified', async () => {
  const result = await render(<CheckboxListContainer />);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render with 0 maxHeight when collapsed', async () => {
  const result = await render(<CheckboxListContainer isCollapsed={true} maxHeight={500} />);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render with default maxHeight when maxHeight is negative', async () => {
  const result = await render(<CheckboxListContainer maxHeight={-10} />);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render with children', async () => {
  const result = await render(
    <CheckboxListContainer>
      <li>1</li>
      <li>2</li>
      <li>3</li>
      <li>4</li>
      <li>5</li>
      <li>6</li>
      <li>7</li>
      <li>8</li>
      <li>9</li>
      <li>10</li>
    </CheckboxListContainer>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});
