import React from 'react';
import styles from './CheckboxListItemContainer.css';
import cx from 'classnames';

export interface Props {
  className?: string;
}

export class CheckboxListItemContainer extends React.Component<Props> {
  render() {
    const { children, className } = this.props;

    return <li className={cx(styles.container, className)}>{children}</li>;
  }
}
