/*
 * Gets the selected option count, exluding parent groupings
 */
export const getSelectedOptionCount = (options: any) =>
  options.reduce((acc: number, option: any) => {
    if (option.options && option.options.length > 0) {
      return acc + option.options.filter((opt: any) => opt.state === 'checked').length;
    }

    if (option.state === 'checked') {
      return acc + 1;
    }

    return acc;
  }, 0);
