import { CheckboxItem } from '../types/index';

export const getSelectedOptions = (options: Array<CheckboxItem>) => {
  return options.reduce((selected: Array<string>, option: CheckboxItem) => {
    if (option.options) {
      option.options.forEach(nestedOption => {
        if (nestedOption.state === 'checked') {
          selected.push(nestedOption.id);
        }
      });
    } else {
      if (option.state === 'checked') {
        selected.push(option.id);
      }
    }

    return selected;
  }, []);
};
