import moment from 'moment';
import React from 'react';
import { render, fireEvent } from 'react-testing-library';
import { DatePicker } from './DatePicker';

describe('DatePicker', () => {
  it('Should show DatePicker', () => {
    const props = {
      date: moment.utc('2017-05-04T00:58:43.488Z'),
      updateDate: () => {},
      locale: 'en-US',
      startRange: moment.utc('2017-05-01T02:00:00.000Z'),
      endRange: moment.utc('2017-05-24T02:00:00.000Z'),
      applyButton: {
        id: 'apply',
        action: () => {},
        label: 'Apply'
      }
    };

    const { container } = render(<DatePicker {...props} />);
    expect(container.firstChild).toMatchSnapshot();
  });

  it('Should show DatePicker with all props passed', () => {
    const updateDateSpy = jest.fn();

    const props = {
      date: moment.utc('2017-05-05T00:58:43.488Z'),
      updateDate: updateDateSpy,
      title: 'Title Here',
      showSelectedDates: true,
      applyButton: {
        id: 'apply',
        action: () => {},
        label: 'Apply'
      }
    };

    const { container, getByText } = render(<DatePicker {...props} />);

    expect(getByText('05/05/2017')).toBeTruthy();
    fireEvent.click(getByText('05/05/2017'));

    fireEvent.click(getByText('15'));
    expect(updateDateSpy).toHaveBeenCalledTimes(1);

    expect(container.firstChild).toMatchSnapshot();
  });
});
