import React from 'react';
import enhanceWithClickOutside from 'react-click-outside';
import { DateRangeSelector, DateRangeSelectorProps } from './DateRangeSelector';

interface EnhancedDateRangeSelectorProps extends DateRangeSelectorProps {
  clickOutside?: () => void;
}

class EnhancedDateRangeSelector extends React.Component<EnhancedDateRangeSelectorProps> {
  handleClickOutside() {
    if (this.props.clickOutside) {
      this.props.clickOutside();
    }
  }
  render() {
    return <DateRangeSelector {...this.props} />;
  }
}

export const EnhancedDateRange = enhanceWithClickOutside(EnhancedDateRangeSelector);
