import React from 'react';
import { render, fireEvent } from 'react-testing-library';
import { DropDown } from './dropdown';

function renderDefaultScenario(clickHandlerSpy: any, isOpen: boolean) {
  const result = render(
    <DropDown open={isOpen} label="dropdown" onLabelClick={clickHandlerSpy}>
      <button>Child1</button>
      <button>Child2</button>
    </DropDown>
  );

  return result;
}

test('Should render a drop down', async () => {
  const result = await renderDefaultScenario(() => {}, false);
  expect(result.container.firstChild).toMatchSnapshot();
});

it('should invoke the callback when clicked', () => {
  const clickHandlerSpy = jest.fn();
  const { getByText, container } = renderDefaultScenario(clickHandlerSpy, true);
  fireEvent.click(getByText('dropdown'));
  expect(clickHandlerSpy).toHaveBeenCalledTimes(1);
  expect(container).toMatchSnapshot();
});
