import cx from 'classnames';
import React from 'react';
import styles from './ErrorIcon.css';

export type IconId = keyof typeof errorIcons;

export interface IconProps {
  className?: string;
  iconId: IconId;
}

export const errorIcons: { [id: string]: SpriteSymbol } = {
  general: require('./assets/general.svg').default,
  async: require('./assets/async.svg').default
};

export class ErrorIcon extends React.Component<IconProps> {
  render() {
    const { className, iconId } = this.props;
    const iconStyles = cx(styles.errorIcon, className);
    const iconHeight = this.props.iconId === 'general' ? '200px' : '96px';

    return (
      <svg data-testid="iconSvg" className={iconStyles} height={iconHeight}>
        <use xlinkHref={`#svg-${iconId}`} />
      </svg>
    );
  }
}
