import React from 'react';
import { ErrorIcon } from './ErrorIcon';
import styles from './ErrorPanel.css';

export type ErrorPanelType = 'general' | 'async';

export interface ViewProps {
  type: ErrorPanelType;
  title: string;
  message: React.ReactNode;
}

export function ErrorPanel(props: ViewProps) {
  return (
    <div className={styles.errorPanel}>
      <ErrorIcon iconId={props.type} />
      <h1>{props.title}</h1>
      <p>{props.message}</p>
    </div>
  );
}
