export type TrackerMethods = 'get' | 'set' | 'send';
export type EventTrackerArgs = [string, any];
export type EventConfig = [TrackerMethods, string, any?];
export type EventQueue = EventConfig[];

export interface SendTimingOptions {
  category: string;
  variable: string;
  value: number;
  label?: string;
}

export interface SendEventOptions {
  category: string;
  action: string;
  label?: string;
  value?: number;
}

export interface SendExceptionOptions {
  description?: string;
  fatal?: boolean;
}
