import React from 'react';
import { Touchable } from './touchable';
import { render } from 'react-testing-library';

describe('Icon', async () => {
  it('Check the rendered touchable', async () => {
    const { getByTestId, getByText } = render(<Touchable iconId="appReporting" text="Reporting" />);
    expect(getByTestId('iconSvg')).toBeTruthy();
    expect(getByText('Reporting')).toBeTruthy();
  });

  it('Check the rendered touchable with right icon position', async () => {
    const { getByTestId, getByText } = render(
      <Touchable iconId="appReporting" text="Reporting" iconPosition="right" />
    );
    expect(getByTestId('iconSvg')).toBeTruthy();
    expect(getByText('Reporting')).toBeTruthy();
  });
});
