import React from 'react';
import { storiesOf } from '@storybook/react';
import { Icon, icons } from './src/icon';
import { Touchable } from './src/touchable';

const buffet = {
  componentName: '@buffet/icon'
};

storiesOf('Icon', module).add('show icon', () => (
  <div style={{ margin: '20px' }}>
    {Object.keys(icons).map(iconId => (
      <div
        style={{
          display: 'inline-block',
          margin: '5px',
          width: '180px',
          height: '90px',
          overflow: 'hidden',
          textAlign: 'center'
        }}
      >
        <Icon iconId={iconId} />
        <br />
        {iconId}
      </div>
    ))}
  </div>
),
{ buffet });

storiesOf('Icon', module).add('show touchable', () => (
  <div style={{ width: '300px' }}>
    <div style={{ margin: '20px' }}>
      <Touchable iconId="appAccounts" text="Accounts" />
    </div>
    <div style={{ margin: '20px' }}>
      <Touchable iconId="appReporting" text="Reporting" />
    </div>
    <div style={{ margin: '20px' }}>
      <Touchable iconId="appReviews" text="Reviews" iconPosition="right" />
    </div>
  </div>
),
{ buffet });
