import React from 'react';
import classnames from 'classnames/bind';
import { Input, InputProps } from './Input';
import { Icon } from '@buffet/icon';
import styles from './InputWithIcon.css';

const cx = classnames.bind(styles);

export interface Props extends InputProps {
  iconId?: string;
  inputClassName?: string;
}

export const InputWithIcon: React.SFC<Props> = props => {
  const { iconId, inputClassName, className, ...rest } = props;
  return (
    <div className={cx(className)}>
      <Icon iconId={iconId || 'magnifyingGlass'} className={cx('inputIcon')} />
      <Input {...rest} className={cx('inputBox', inputClassName)} />
    </div>
  );
};
