import React from 'react';
import { render } from 'react-testing-library';

import ContentLayer, { StackingIndex } from './ContentLayer';


test('Should render a global Content Layer inside a default Portal container', async () => {
  const result = render(
    <ContentLayer layerType={StackingIndex.Modal} global={true}>
      <p>test</p>
    </ContentLayer>,
    { container: document.body }
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a global Content Layer inside a specific Portal container', async () => {
  const result = render(
    <ContentLayer layerType={StackingIndex.Modal} global={true}>
      <p>test</p>
    </ContentLayer>,
    { container: document.body }
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a local Content Layer', async () => {
  const result = render(
    <ContentLayer layerType={StackingIndex.Modal}>
      <p>test</p>
    </ContentLayer>,
    { container: document.body }
  );

  expect(result.container.firstChild).toMatchSnapshot();
});
