import React from 'react';
import classNames from 'classnames/bind';
import styles from './nav.css';

const cx = classNames.bind(styles);

interface Props {
  title?: string;
}

export const NavIcon: React.SFC<Props> = props => (
  <span className={cx('gcaNavIcon')} title={props.title}>
    {props.children}
  </span>
);
