import React from 'react';
import { render } from 'react-testing-library';
import { RadioButtonGroup } from './RadioButtonGroup';

async function renderDefaultScenario() {
  const childProvider = [
    { value: 'fee', label: 'Say Fee!' },
    { value: 'fi', label: 'Say Fi!' },
    { value: 'fo', label: 'Say Fo!' }
  ];
  const result = render(<RadioButtonGroup onChange={() => {}} selectedValue="fi" childProvider={childProvider} />);

  return result;
}

test('Should render radio button group', async () => {
  const result = await renderDefaultScenario();
  expect(result.container.firstChild).toMatchSnapshot();
});
