import React from 'react';
import { render } from 'react-testing-library';
import { SimpleRadioButtonGroup } from './SimpleRadioButtonGroup';
import { RadioButton } from './RadioButton';

async function renderDefaultScenario() {
  const result = render(
    <SimpleRadioButtonGroup onChange={() => {}} selectedValue="fi">
      <RadioButton value="fee" label="Say Fee!" onChange={() => {}} />
      <RadioButton value="fi" label="Say Fi!" onChange={() => {}} />
      <RadioButton value="fo" label="Say Fo!" onChange={() => {}} />
    </SimpleRadioButtonGroup>
  );

  return result;
}

test('Should render a simple radio button', async () => {
  const result = await renderDefaultScenario();
  expect(result.container.firstChild).toMatchSnapshot();
});
