/* istanbul ignore file */

/**
 * This function prefixes zeroes before a given number
 * example usage would be padding hour from 3 to 03
 *
 * @param str - a number to be prefixed with zeroes
 * @param size - number of digits to be present in the final result
 * @returns either the return value of your initializer, or the result of a previously created instance.
 */
export function prefixZerosToNumber(str: number, size: number = 2) {
  let prefixedNum = String(str);
  while (prefixedNum.length < size) {
    prefixedNum = '0' + prefixedNum;
  }
  return prefixedNum;
}
