import { Button } from '@buffet/button';
import { Icon } from '@buffet/icon';
import React from 'react';

import './BackButton.scss';

export interface Props {
  label?: React.ReactNode;
  onClick: () => void;
  testId?: string;
}

const BackButton: React.FunctionComponent<Props> = ({
  onClick,
  label,
  children,
  testId,
}) => (
  <Button
    type="button"
    theme="bare"
    size="xsmall"
    onClick={onClick}
    className="generic-back-btn"
    testId={testId}
  >
    <Icon iconId="chevronLeft" className="back-chevron" />
    {children || label}
  </Button>
);

export default BackButton;
