// Allow importing of markdown files
declare module '*.md' {
  const text: string;
  export default text;
}

// Allow importing of JSON files
declare module '*.json' {
  const text: string;
  export default text;
}

// Allow importing of CSS files
declare module '*.css' {
  const styles: any;
  export default styles;
}

// Allow importing of CSS files
declare module '*.scss' {
  const styles: any;
  export default styles;
}