export type IconId = keyof typeof icons;

export class Icon extends React.Component<IconProps> {
  render() {
    const { className, iconId } = this.props;
    const iconStyles = cx(styles.icon, className);

    return (
      <svg data-testid="iconSvg" className={iconStyles} viewBox="0 0 24 24">
        <use xlinkHref={`#svg-${iconId}`} />
      </svg>
    );
  }
}
