import React from 'react';
import PropTypes from 'prop-types';
import { TickLabel } from './TickLabel';

class Tick extends React.Component {
  static propTypes = {
    label: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.array,
      PropTypes.element,
    ]),
    styles: PropTypes.object,
    theme: PropTypes.object.isRequired,
  }

  static defaultProps = {
    label: '',
    styles: {},
  }

  render() {
    const { label, styles, theme } = this.props;
    const { fill } = theme.axis.x.stroke;

    return (
      <g>
        <rect
          y="-20"
          width="1"
          height="8"
          fill={fill}
        >
        </rect>
        <TickLabel
          label={label}
          styles={styles.label}
          theme={theme}
        />
      </g>
    );
  }
}

export { Tick };
