const LENGTH = 1;
const PREFIX = '_';

export const generateId = (length: number = LENGTH, prefix: string = PREFIX) => {
  let i = 0;
  let id = '';

  if (length <= 0) {
    throw new Error(`Length must be 1 or more. Length: ${length}`);
  }

  // @TODO: Remove reliance on this loop
  // tslint:disable
  do {
    id += Math.random().toString(36).substr(2, 9);
    i += 1;
  } while (i < length);
  // tslint:enable

  return `${prefix}${id}`;
};
