import React from 'react';
import { Redirect, Route, Switch } from 'react-router-dom';

import { Routes } from '../common/App/App';
import { PermissionsDashboard } from '../common/Permissions/PermissionsDashboard';
import { GroupsSearch } from './GroupsSearch';

export default function GroupsDashboard() {
  return (
    <Switch>
      <Route path={Routes.GidPermissions} render={(p) => <PermissionsDashboard {...p} idType="gid" />} />
      <Route path={Routes.Groups} component={GroupsSearch} />
      <Redirect to={Routes.Groups} />
    </Switch>
  );
}
