import React from 'react';
import { Redirect, Route, Switch } from 'react-router-dom';

import { Routes } from '../common/App/App';

import { PermissionsDashboard } from '../common/Permissions/PermissionsDashboard';
import { Agreements } from '../Restaurants/Agreements/Agreements';
import { RidDiagnosticDashboard } from '../Restaurants/RidDiagnostic/RidDiagnosticDashboard';
import { RestaurantEverclear } from './Everclear/RestaurantEverclear';
import { RestaurantSearch } from './RestaurantSearch';

export default function RestaurantsDashboard() {
  return (
    <Switch>
      <Route path={Routes.RidEverclear} component={RestaurantEverclear} />
      <Route path={Routes.RidDiagnostic} component={RidDiagnosticDashboard} />
      <Route path={Routes.RidPermissions}  render={(p) => <PermissionsDashboard {...p} idType="rid" />} />
      <Route path={Routes.RidAgreements} component={Agreements} />
      <Route path={Routes.Restaurants} component={RestaurantSearch} />
      <Redirect to={Routes.Restaurants} />
    </Switch>
  );
}

