import React from 'react';
import { Redirect, Route, Switch } from 'react-router-dom';

import { Routes } from '../common/App/App';

import { UserNew } from './UserNew';
import { UserProfile } from './UserProfile';
import { UsersSearch } from './UsersSearch';

export default function UsersDashboard() {
  return (
    <Switch>
      <Route exact={true} path={Routes.Users} component={UsersSearch} />
      <Route exact={true} path={Routes.UserNew} component={UserNew} />
      <Route exact={true} path={Routes.UserProfile} component={UserProfile} />
      <Redirect to={Routes.Users} />
    </Switch>
  );
}
