import { Chevron } from 'gca-react-components';
import React, { SFC } from 'react';
import { FormSection } from 'redux-form';
import Times from './Times';

interface Props {
  expanded: boolean;
  label: string;
  onClickDay(value: any): void;
}

const DayAndTimes: SFC<Props> = ({ expanded, label, onClickDay }) => (
  <li>
    <div className="chevron-label-container" onClick={() => onClickDay(!expanded)}>
      <Chevron color="#5BAFE6" direction={expanded ? 'up' : 'down'} height="15" />
      <span>{label}</span>
    </div>
    {expanded && (
      <FormSection name={label.toLowerCase()}>
        <Times />
      </FormSection>
    )}
  </li>
);

export default DayAndTimes;
