import { mount } from 'enzyme';
import React from 'react';
import { DaysAndTimesSummary } from './DaysAndTimesSummary';

const renderComponent = (props: any) => mount(<DaysAndTimesSummary {...props} />);

describe('<DaysAndTimesSummary />', () => {
  const someChecked = {
    monday: {
      evening: true,
      midDay: true,
      morning: false,
    },
    tuesday: {
      morning: true,
    },
    wednesday: {},
  };
  test('shows Monday evening, midday. Tuesday morning', () => {
    const wrapper = renderComponent(someChecked);

    expect(wrapper.debug()).toMatchSnapshot();
  });
});
