import { Checkbox } from 'gca-react-components';
import React, { SFC } from 'react';
import { Field } from 'redux-form';
import CheckIcon from './images/CheckIcon';
import EveningIcon from './images/EveningIcon';
import MiddayIcon from './images/MiddayIcon';
import MorningIcon from './images/MorningIcon';

interface Props {
  short: string;
  time: string;
  name: string;
}

const Time: SFC<Props> = ({ short, time, name }) => (
  <li>
    <Field
      className="time-checkbox"
      component={({ input }: any) => {
        return (
          <Checkbox
            {...input}
            label={
              <div className="time-label">
                <div className="icons">
                  <div className="unselected-time-icon">
                    {short === 'Morning' && <MorningIcon />}
                    {short === 'Mid-day' && <MiddayIcon />}
                    {short === 'Evening' && <EveningIcon />}
                  </div>
                  <div className="selected-time-icon">
                    <CheckIcon />
                  </div>
                </div>
                <span className="short">{short}</span>
                <span className="time">{time}</span>
              </div>
            }
            onClick={input.onChange}
          />
        );
      }}
      name={name}
      type="checkbox"
    />
  </li>
);

export default Time;
