import { mount } from 'enzyme';
import React from 'react';
import { timesOrdering } from './constants';
import Times from './Times';

jest.mock('./Time', () => () => 'Time');

const renderComponent = () => mount(<Times />);

describe('<Times />', () => {
  test('Renders 3 "Time"s', () => {
    const wrapper = renderComponent();

    const times = wrapper.find('ul').children();
    expect(times.length).toEqual(timesOrdering.length);

    expect(wrapper.debug()).toMatchSnapshot();
  });
});
