import React, { SFC } from 'react';
import { FormattedMessage } from 'react-intl';
import { CreditsModel } from '../../store/customPromotions/customPromotions.types';

interface Props {
  credits: CreditsModel;
}

const CreditsInfoList: SFC<Props> = ({ credits }) => {
  const { currencySymbol, creditAmount, defaultCovers, formattedCoverPrice } = credits;
  const values: { [index: string]: string } = {
    formattedCoverPrice,
    formattedCreditAmount: `${currencySymbol}${creditAmount}`,
    formattedNumCovers: `${defaultCovers}`,
  };

  const items = ['formattedCreditAmount', 'formattedNumCovers', 'formattedCoverPrice'];

  return (
    <ul className="credits-info-list">
      {items.map((item, itemNumber) => (
        <FormattedMessage
          id={`guestcampaigns.custom_promotions.education.2.credits.${itemNumber + 1}`}
          tagName="li"
          key={itemNumber}
          values={{
            /* So we can bold these items */
            [item]: <strong className="formatted-value">{values[item]}</strong>,
          }}
        />
      ))}
    </ul>
  );
};

export default CreditsInfoList;
