import React from 'react';
import { FormattedMessage, InjectedIntlProps, injectIntl } from 'react-intl';
import { UICard } from '../../../../shared_modules/shared-react-atomic-ui/UICard';
interface SP {
  app: {
    name: string;
    enabled: boolean;
    url: string;
  };
  image: string;
}

type Props = SP & InjectedIntlProps;

class AppCard extends React.Component<Props> {
  public constructor(props: any) {
    super(props);
  }


  public render = () => {
    const {
      app: { enabled, name, url },
      intl: { formatMessage },
    } = this.props;
    const { image } = this.props;
    const cta = formatMessage({ id: `card.${name}.cta` });
    const description = formatMessage({ id: `card.${name}.description` });
    const title = formatMessage({ id: `card.${name}.title` });
    const imageComponent: any = <img src={image} />;

    return (
      <UICard
        cta={cta}
        ctaUrl={url}
        linkable={enabled}
        description={description}
        key={title}
        imageComponent={imageComponent}
        title={title} />
    );
  };
}

export default injectIntl(AppCard);
export { AppCard as PureAppCard };
