import { mount } from 'enzyme';
import React from 'react';
import { IntlProvider } from 'react-intl';
import { Provider } from 'react-redux';
import configureMockStore from 'redux-mock-store';
import { keys } from '../translations/bundles/marketing.json';
import GiftsLanding from './GiftsLanding';

describe('<GiftsLanding />', () => {
  it('should fire GIFTS/CLICK_CALL_TO_ACTION when cta clicked', () => {
    const store = configureMockStore()({});
    const wrapper = mount(
      <Provider store={store}>
        <IntlProvider locale="en-US" messages={keys}>
          <GiftsLanding />
        </IntlProvider>
      </Provider>,
    );
    const button = wrapper.find('button').first();
    expect(store.getActions().length).toEqual(0);
    button.simulate('click');
    expect(store.getActions()[0].type).toEqual('GIFTS/CLICK_CALL_TO_ACTION');
  });
});
