import React, { SFC } from 'react';

import { FormattedMessage } from 'react-intl';
import DetailLine, { DetailLineCommonProps } from './DetailLine';

interface ValueProps {
  targets: string[];
}

type Props = ValueProps & DetailLineCommonProps;

const Targets: SFC<Props> = ({ targets, ...rest }) => (
  <DetailLine
    value={targets.map(key => <FormattedMessage key={key} id={`guestcampaigns.list.guest_targeting.${key}`} />)}
    valueClass="targets"
    {...rest}
  />
);

export default Targets;
