import React from 'react';
import { InjectedIntl } from 'react-intl';
import makeDates from './make-dates';

const renderSuppressionDates = (
  intl: InjectedIntl,
  suppressionDates: any,
  defaultSuppressionDates: any,
  editListItem: JSX.Element | null,
) => {
  const dates = makeDates(intl, suppressionDates, defaultSuppressionDates);
  return (
    <section className="suppression-dates">
      <ul>
        {dates.length > 0 ? dates.map((date, index) => <li key={index}>{date}</li>) : <li>â€”</li>}
        {editListItem}
      </ul>
    </section>
  );
};

export default renderSuppressionDates;
