import { mount } from 'enzyme';
import React from 'react';
import OTKitIcon from './OTKitIcon';

describe('<OTKitIcon />', () => {
  it('can replace the fill color', () => {
    const props = {
      fill: '#aabbcc',
      name: 'icSuccess',
    };
    const wrapper = mount(
      <OTKitIcon {...props as any} />,
    );

    expect(wrapper.html()).toMatch('#aabbcc');
  });
});
