import icons, { iconSize as defaultIconSize } from 'otkit-icons/token.common';
import React, { SFC } from 'react';
import './OTKitIcon.scss';

const OTKitIcon: SFC<{ fill?: string; name: string; iconSize?: string }> = ({ fill, name, iconSize }) => {
  let html = icons[name];
  const size = iconSize || defaultIconSize;
  if (fill !== undefined) {
    html = html.replace(/fill="#\w+"/i, `fill="${fill}"`);
  }

  return <i className="otkit-icon" dangerouslySetInnerHTML={{ __html: html }} style={{ height: size, width: size }} />;
};

export default OTKitIcon;
