import React from 'react';
import { IntlProvider } from 'react-intl';
import { create } from 'react-test-renderer';
import { keys } from '../../../translations/bundles/marketing.json';
import Success, { SuccessType } from './Success';

describe.skip('<Success />', () => {
  const scenarios = [
    {
      description: 'flexible pricing',
      location: { search: `?type=${SuccessType.FlexiblePricing}` },
    },
    {
      description: 'original pricing',
      location: { search: `?type=${SuccessType.OriginalPricing}` },
    },
  ];

  scenarios.forEach(({ description, location }) => {
    it(description, () => {
      const component = create(
        <IntlProvider locale="en-US" messages={keys}>
          <Success location={location} />
        </IntlProvider>,
      );
      const tree = component.toJSON();
      expect(tree).toMatchSnapshot();
    });
  });
});
