import { shallowWithIntl } from 'gca-jest-config';
import { shallowToJson } from 'enzyme-to-json';
import React from 'react';
import SpeechDialog from './SpeechDialog';

describe('<SpeechDialog />', () => {
  it('should render', () => {
    const wrapper = shallowWithIntl(<SpeechDialog />);
    expect(shallowToJson(wrapper)).toMatchSnapshot();
  });

  it('can render a header component', () => {
    const HeaderComponent = () => (
      <h1>Test Header</h1>
    );
    const header = <HeaderComponent />;
    const wrapper = shallowWithIntl(<SpeechDialog header={header} />);
    expect(shallowToJson(wrapper)).toMatchSnapshot();
  });
});

