describe('Open main permission-required page', function() {

  beforeEach(() => {
    cy.fixture(`credentials.json`)
      .then(credentials => {
        cy.login(credentials.username, credentials.password);
      });
  });

  it('Open page', () => {
    cy.visit('/permission-required');
    cy.get('h1').should('contain','Permission Required');
  });
})
