import React from 'react';
import { FormattedMessage } from 'react-intl';
import { UpserveVerifyResponse } from '../../store/types';

import './CompareAddresses.scss';

interface Props {
  addresses: UpserveVerifyResponse;
}

export class CompareAddresses extends React.Component<Props> {
  public render() {
    const {
      opentable_restaurant_details: otAddress,
      partner_restaurant_details: partnerAddress,
    } = this.props.addresses;
    return (
      <div className="compare-addresses">
        <div className="explanation">
          <FormattedMessage tagName="h2" id="nextSteps.pos.upserve.compare.header" />
          <FormattedMessage tagName="div" id="nextSteps.pos.upserve.compare.content" />
        </div>
        <div className="side-by-side">
          <div className="opentable-address">
            <FormattedMessage tagName="h3" id="nextSteps.pos.upserve.compare.opentable" />
            <div data-testid="ot-name">{otAddress.name}</div>
            <div data-testid="ot-street1">{otAddress.street1}</div>
            {otAddress.street2 && <div data-testid="ot-street2">{otAddress.street2}</div>}
            <div>
              <span data-testid="ot-city">{otAddress.city}</span>
              {', '}
              <span data-testid="ot-state">{otAddress.state}</span>
            </div>
            <div data-testid="ot-country">{otAddress.country}</div>
            <div data-testid="ot-zip">{otAddress.zip}</div>
          </div>
          <div className="partner-address">
            <FormattedMessage tagName="h3" id="nextSteps.pos.upserve.compare.upserve" />
            <div data-testid="partner-name">{partnerAddress.name}</div>
            <div data-testid="partner-street1">{partnerAddress.street1}</div>
            {partnerAddress.street2 && <div data-testid="partner-street2">{partnerAddress.street2}</div>}
            <div>
              <span data-testid="partner-city">{partnerAddress.city}</span>
              {', '}
              <span data-testid="partner-state">{partnerAddress.state}</span>
            </div>
            <div data-testid="partner-country">{partnerAddress.country}</div>
            <div data-testid="partner-zip">{partnerAddress.zip}</div>
          </div>
        </div>
      </div>
    );
  }
}
