import React from 'react';
import { connect } from 'react-redux';
import OnboardingDisplayState from '../PosOnboarding/OnboardingDisplayState';
import { OnboardingDisplayStateName, State } from '../store/types';
import OnboardingFooter from './OnboardingFooter';

interface SP {
  displayState: OnboardingDisplayStateName;
}

type Props = SP;

class PosOnboardingView extends React.Component<Props> {
  public render() {
    return (
      <>
        <OnboardingDisplayState displayKey={this.props.displayState} />
        {(this.props.displayState === 'posSelect' || this.props.displayState === 'nextSteps') && <OnboardingFooter />}
      </>
    );
  }
}

const mapState = (state: State): SP => ({
  displayState: state.onBoarding.displayState,
});

const PosOnboarding = connect<SP, {}, {}>(
  mapState,
  {},
)(PosOnboardingView);
export default PosOnboarding;
export { PosOnboardingView as PurePosOnboarding, Props };
