import React from 'react';
import './Toggle.scss';

interface Props {
  enabled: boolean;
  onText: string;
  offText: string;
  onClick: () => void;
}

class Toggle extends React.Component<Props, {}> {
  private activeClass = 'toggle-active';
  private inactiveClass = 'toggle-inactive';
  public render() {
    return (
      <button className="toggle" onClick={this.props.onClick}>
        <div className={`toggle-left toggle-button ${this.props.enabled ? this.activeClass : this.inactiveClass}`}>
          {this.props.onText}
        </div>
        <div className={`toggle-right toggle-button ${this.props.enabled ? this.inactiveClass : this.activeClass}`}>
          {this.props.offText}
        </div>
      </button>
    );
  }
}

export { Toggle, Props };
