import { shallow } from 'enzyme';
import React from 'react';
import SubmittedLeadGenPage from './SubmittedLeadGenPage';

describe('<SubmittedLeadGenPage />', () => {
  const setup = () => {
    const submittedLeadGen = shallow(<SubmittedLeadGenPage />);

    return { submittedLeadGen };
  };

  it('should should have a header and message', () => {
    const { submittedLeadGen } = setup();

    expect(submittedLeadGen.find('h1').length).toBe(1);
    expect(submittedLeadGen.find('p').length).toBe(1);
  });
});
