type init = 'init';
type started = 'started';
type done = 'done';
type failed = 'failed';

export interface InitState {
  status: init;
  data: undefined;
}

export interface StartedState {
  status: started;
  data: undefined;
}

export interface DoneState<T> {
  status: done;
  data: T;
}

export interface FailedState {
  status: failed;
  data: any;
}

export type AsyncStatus = init | started | done | failed;

export type AsyncState<T> = InitState | StartedState | DoneState<T> | FailedState;
