import { shallow } from 'enzyme';
import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { DateFilterItem, PurePreset } from './Preset';

describe('<PurePreset />', () => {
  let props;

  beforeEach(() => {
    props = {
      pending: '30',
      set: jest.fn(),
    };
  });

  it('Default snapshot', () => {
    const wrapper = shallowWithIntl(<PurePreset {...props} />);

    expect(wrapper).toMatchSnapshot();
  });
});

describe('<DateFilterItem />', () => {
  let props;

  beforeEach(() => {
    props = {
      messageId: 'foo',
      range: '30',
      set: jest.fn(),
    };
  });

  it('Default snapshot', () => {
    const wrapper = shallowWithIntl(<DateFilterItem {...props} />);

    expect(wrapper).toMatchSnapshot();
  });
});
